package uk.gov.dvla.ida.serviceprovider.adapter;

public class AuthnResponseValidator {
    String testHubResponseSuccess = "";
    String testHubResponseSuccessNoMatch = "";
    String testHubAuthFailure = "";
    String testHubNoAuthContext = "";


    public AuthenticationResult validate(String authnResponse) {
        if (authnResponse.equals(testHubResponseSuccess))
            return new AuthenticationResult("pid", AuthenticationResult.Status.SUCCESS);
        if (authnResponse.equals(testHubResponseSuccessNoMatch))
            return new AuthenticationResult("pid", AuthenticationResult.Status.SUCCESS_NO_MATCH);
        if (authnResponse.equals(testHubAuthFailure))
            return new AuthenticationResult("pid", AuthenticationResult.Status.FAILURE);

        return new AuthenticationResult("pid", AuthenticationResult.Status.CANCELED);
    }
}
